import React, { Component } from 'react'
import NewsItem from './NewsItem'
import img from '../images/unknown.png'


export default class News extends Component {
   constructor() {
    super();
    this.state = {
        articales : [],
        loading : false
    }
   }
  async componentDidMount() {
    const url = "https://newsapi.org/v2/top-headlines?country=sa&category=business&apiKey=d5653c3018cd4b7eb10d5feda36994e8"
    let newsData = await fetch(url);
    let parsedData = await newsData.json();
    console.log(parsedData);
    this.setState({
        articales : parsedData.articles
    })
   }
  render() {

    return (
        <>
     <div className="container my-3">
        <h2> Top HeadLines</h2>
        <div className="row my-3 ">
            {this.state.articales.map((curElm)=>{return(
                <div className="col-md-4 my-1" key={curElm.url}>
                <NewsItem title={curElm.title?curElm.title.slice(0, 35) + "..." : ""} description={curElm.description?curElm.description.slice(0, 88)+ "...": ""} Imageurl={curElm.urlToImage} newsUrl={curElm.url}/>
                </div>
            ) })}

      </div>
      </div>
      </>
    )
  }
}
